import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square (squares) {
      return (
        <button className="square"  onClick={squares.onClick}>
          {squares.value}
        </button>
      );
  }

  class Board extends React.Component {
    constructor(squares) {
      super(squares);
      this.state = {
        boxes: Array(9).fill(null),
      };
    }
    handleClick(i) {
      const boxes = this.state.boxes.slice();
      if (checkWinner(boxes) || boxes[i]) {
      return;
      }
      boxes[i] = this.state.nextTurn ? 'X' : 'O';
      this.setState({
        boxes: boxes,
        nextTurn: !this.state.nextTurn,
      });
    }
    renderSquare(i) {
      return (
        <Square 
          value={this.state.boxes[i]} 
          onClick={() => this.handleClick(i)}
      />
      );
    }

    render() {
      const winner = checkWinner(this.state.boxes);
      let updateTurn;
      let currentPlayer;
      if (winner) {
        updateTurn = 'Winner" '+ winner;
      } else {
          if (this.state.nextTurn) {
            currentPlayer = "Player 1";
          } else {
              currentPlayer = "Player 2";
          }
        updateTurn = currentPlayer + "'s turn"
      }
      return (
        <div>
          <div className="updateTurn">{updateTurn}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }

  function Game () {
    // render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
        </div>
      );
    // }
  }

  function checkWinner (boxes) {
    const checkRows = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < checkRows.length; i++) {
      const [a, b, c] = checkRows[i];
      if (boxes[a] && boxes[a] === boxes[b] && boxes[a] === boxes[c]) {
        return boxes[a];
      }
    }
    return null;
  }

  // ========================================
  ReactDOM.render(
    Game(),
    document.getElementById('root')
  );
