// Main component for tic-tac-toe board
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

ReactDOM.render (
    playGame(),
    document.getElementById('root')
);
function getBoxes (squares) {
    return (
        <button className="Box" onClick={squares.onClick}>{squares.value}</button>
    )
}
function playGame () {
      return (
        <div className="game">
          <div className="game-board">
            showBoard();
          </div>
        </div>
      );
  }

  class Board extends React.Component {
    constructor(squares) {
      super(squares);
      this.state = {
        boxes: Array(9).fill(null),
      };
    }
    handleClick(i) {
      const boxes = this.state.boxes.slice();
      if (checkWinner(boxes) || boxes[i]) {
      return;
      }
      boxes[i] = this.state.nextX ? 'X' : 'O';
      this.setState({
        boxes: boxes,
        nextX: !this.state.nextX,
      });
    }
    renderSquare(i) {
      return (
        <getBoxes 
          value={this.state.boxes[i]} 
          onClick={() => this.handleClick(i)}
      />
      );
    }

    render() {
      const winner = checkWinner(this.state.boxes);
      let update;
      if (winner) {
        update = 'The winner" '+ winner;
      } else {
        update = (this.state.nextX ? 'X' : 'O') + ", it is your turn!";
      }
      return (
        <div>
          <div className="update">{update}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }

  function checkWinner (boxes) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (boxes[a] && boxes[a] === boxes[b] && boxes[a] === boxes[c]) {
        return boxes[a];
      }
    }
    return null;
  }
