import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square (squares) {
      return (
        <button className="singleBox"  onClick={squares.onClick}>
          {squares.value}
        </button>
      );
  }

  class Board extends React.Component {
    // constructor(squares) {
    //   super(squares);
    //   this.state = {
    //     boxes: Array(9).fill(null),
    //   };
    // }

    updateSelected(num) {
      const boxes = this.state.squares.slice();
      if (checkWinner(squares) || squares[num]) {
      return;
      }
      squares[num] = this.state.nextTurn ? 'X' : 'O';
      this.setState({
        squares: squares,
        nextTurn: !this.state.nextTurn,
      });
    }
    updateSquare(num) {
      return (
        <Square 
          value={this.props.squares[num]} 
          onClick={() => this.props.onClick(num)}
      />
      );
    }
    restartGame () {
        this.state = {
          boxes: Array(9).fill(null),
        }
        for (let x = 0; x < 10; x++) {
            this.updateSelected(x);
        }
  }
    render() {
        console.log("state: ", this.state);
        const history = this.state.history;
        const currentMoves = history[history.length -1];

     let filled = false;
     if (this.state.nextTurn == undefined) {
        this.state.nextTurn = true;
     }
      const winner = checkWinner();
      const boxes = currentMoves.squares.slice();
      if (!boxes.includes(null)) {
        filled = true;
      }
      let updateTurn;
      let currentPlayer;
      if (winner) {
        updateTurn = winner + " wins!";
      } else if (filled) {
          updateTurn = "Tie Game!"
      } else {
          if (this.state.nextTurn) {
            currentPlayer = "Player 1";
          } else {
              currentPlayer = "Player 2";
          }
        updateTurn = currentPlayer + "'s turn"
      }
      return (
        <div>
          {winner || filled && <h3 className="endGame">{updateTurn}</h3> }
          {!winner && !filled && <h3 className="updateTurn">{updateTurn}</h3>}

          <div>
            {this.updateSquare(0)}
            {this.updateSquare(1)}
            {this.updateSquare(2)}
          </div>
          <div>
            {this.updateSquare(3)}
            {this.updateSquare(4)}
            {this.updateSquare(5)}
          </div>
          <div >
            {this.updateSquare(6)}
            {this.updateSquare(7)}
            {this.updateSquare(8)}
          </div>
          <button className="resetButton" onClick={() => this.restartGame()}>Reset Game</button>

        </div>

      );
    }
  }
  class Game extends React.Component {
      constructor (props) {
          super(props);
          this.state = {
              history: [{
                  squares: Array(9).fill(null),
              }],
              nextTurn: true,
          }
      }
      render () {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
        </div>
      );
      }
  }
  function checkWinner (boxes, filled) {
    const checkRows = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < checkRows.length; i++) {
      const [a, b, c] = checkRows[i];
      if (boxes[a] && boxes[a] === boxes[b] && boxes[a] === boxes[c]) {
        return boxes[a];
      } 
    }
    return null;
  }

  // ========================================
  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
